INSERT INTO `service_zoo` (`id_service`, `nom_service`, `description_service`, `images_services`, `horaires_services`) VALUES
(1, 'Les Maitres des Airs', '\r\n          \r\nC’est durant l’hiver 2011 que l’équipe du Parc Arcadia des Alpes a transformé l’ancien espace des macaques (magots) en une aire de spectacle pour les oiseaux.\r\n\r\nL’objectif de ce spectacle unique est de présenter des rapaces mais aussi des oiseaux rares, peu connus et rarement visibles dans les spectacles. Bien sûr cette représentation se veut pédagogique, elle est donc animée par l’un de nos fauconniers qui vous présentera naturellement le comportement de nos oiseaux, leurs techniques de chasse, de vols ou encore leur alimentation.\r\n\r\nAujourd’hui, c’est plus de 4 espèces d’oiseaux qui évoluent autour de vous lors de ce spectacle. Un véritable festival de couleurs avec un final époustouflant, de quoi vous laisser des souvenir inoubliables.\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', 'https://cdn-images.zoobeauval.com/P40JeDUVgpQxkExeaFr3fNhCu0Q=/1400x831/https%3A%2F%2Fs3.eu-west-3.amazonaws.com%2Fimages.zoobeauval.com%2F2020%2F05%2F01-maitresdesairs-header-5ed0d74c93d85.jpg', 'TOUS LES JOURS\r\n\r\n(selon conditions météo et selon affluence)\r\n\r\nDu 30/03 au  05/07/2024 :  à 16h15\r\n\r\nDu 06/07 au 01/09/2024 : à 14h25 et 16h15\r\n\r\nDu 02/09 au 06/10/2024 : à 15h\r\n\r\n\r\n35 minutes\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n'),
(3, 'Les Activités ludiques ', 'Vos enfants ont envie de devenir de véritables spécialistes de la biodiversité ? Des livrets de jeux adaptés à tous les âges sont disponibles à la Maison Beauval Nature et dans nos boutiques.\r\n\r\nLe prix d’un carnet est intégralement reversé à l’association Beauval Nature. Une façon ludique de soutenir l’association ! Une fois leur carnet complété, les enfants pourront recevoir des récompenses !\r\n\r\nVenez en apprendre plus sur le rôle de l’association Beauval Nature et découvrir les programmes de conservation qu’elle soutient.', 'https://cdn-images.zoobeauval.com/e09t5n00gf8c25SMJh61DJeW_IY=/800x600/https%3A%2F%2Fs3.eu-west-3.amazonaws.com%2Fimages.zoobeauval.com%2F2024%2F03%2Factivites-maison-bn-02-65faa81fcee9c.jpg', '');

